pragma solidity 0.5.12;
import "openzeppelin-solidity/contracts/token/ERC20/ERC20Mintable.sol";

contract Airdrop {
  mapping (address => bool) public admins;
  // receiverAddress => airdrop details
  mapping (address => AirdropDetail[]) public airdropPerUser;
  // airdropID => airdrop detail
  mapping (uint256 => AirdropDetail) public airdrops;
  // airdropID => isWithdraw
  mapping (uint256 => bool) public airdropWithdrawals;
  uint256 public airdropID = 0;

  event AirdropWithdrew (uint256 airdropID, address receiver, address tokenAddress, uint256 airdropAmount, uint256 timelock);
  event AirdropAdded (uint256 airdropID, address receiver, address tokenAddress, uint256 airdropAmount, uint256 timelock, address adder);

  struct AirdropDetail {
    uint256 airdropID;
    address receiverAddress;
    address tokenAddress;
    uint256 airdropAmount;
    uint256 timelock;
  }

  constructor () public {
    admins[msg.sender] = true;
  }

  function addAirdrop(address _receiverAddress, address _tokenAddress, uint256 _airdropAmount, uint256 _timelock) public onlyAdmin {
    airdropID += 1;
    airdropPerUser[_receiverAddress].push(AirdropDetail(airdropID, _receiverAddress, _tokenAddress, _airdropAmount, _timelock));
    airdrops[airdropID] = AirdropDetail(airdropID, _receiverAddress, _tokenAddress, _airdropAmount, _timelock);
    airdropWithdrawals[airdropID] = false;
    emit AirdropAdded(airdropID, _receiverAddress, _tokenAddress, _airdropAmount, _timelock, msg.sender);
  }

  function getAirdropLengthByUserAddress(address _receiver) public view returns (uint256) {
    return airdropPerUser[_receiver].length;
  }

  function withdrawAirdrop(uint256 _airdropID) public {
    AirdropDetail storage selectedAirdrop = airdrops[_airdropID];
    require(selectedAirdrop.receiverAddress == msg.sender, "Only tokens owner can withdraw.");
    require(airdropWithdrawals[_airdropID] == false, "This airdrop was withdrew.");
    require(selectedAirdrop.timelock <= block.timestamp, "This airdrop is on time locked.");
    ERC20Mintable(selectedAirdrop.tokenAddress).transfer(msg.sender, selectedAirdrop.airdropAmount);
    airdropWithdrawals[_airdropID] = true;
    emit AirdropWithdrew(selectedAirdrop.airdropID, selectedAirdrop.receiverAddress, selectedAirdrop.tokenAddress, selectedAirdrop.airdropAmount, selectedAirdrop.timelock);
  }

  function updateAdmin(address _admin, bool _enable) public onlyAdmin {
    admins[_admin] = _enable;
  }

  modifier onlyAdmin() {
    require(admins[msg.sender] == true, "This address is not an admin.");
    _;
  }
}
